declare(strict_types=1);

namespace PrestaShop\PrestaShop\Core\Domain\Product\ValueObject;

use PrestaShop\PrestaShop\Core\Domain\Product\Exception\ProductConstraintException;

/**
 * Holds product mpn code value
 */
class Mpn
{
    /**
     * Maximum allowed symbols for mpn code
     */
    const MAX_LENGTH = 40;

    /**
     * @var string
     */
    private $value;

    /**
     * @param string $value
     */
    public function __construct(string $value)
    {
        $this->assertMpnIsValid($value);
        $this->value = $value;
    }

    /**
     * @return string
     */
    public function getValue(): string
    {
        return $this->value;
    }

    /**
     * @param string $value
     *
     * @throws ProductConstraintException
     */
    private function assertMpnIsValid(string $value): void
    {
        if (strlen($value) <= self::MAX_LENGTH) {
            return;
        }

        throw new ProductConstraintException(
            sprintf(
                'Invalid MPN length. Maximum allowed length is %s symbols',
                self::MAX_LENGTH
            ),
            ProductConstraintException::INVALID_MPN
        );
    }
}
